class MultiselectAdapter(private val items: List<Product>) : RecyclerView.Adapter<MultiselectAdapter.ViewHolder>() {

    // Map to store the checked state for each item
    private val checkedStateMap = mutableMapOf<Int, Boolean>()

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        val view = LayoutInflater.from(parent.context).inflate(R.layout.item_multiselect, parent, false)
        return ViewHolder(view)
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        val item = items[position]
        holder.bind(item, checkedStateMap[position] ?: false) // Use the stored state or default to false
    }

    override fun getItemCount(): Int {
        return items.size
    }

    fun getCheckedItems(): List<Product> {
        return checkedStateMap.filter { it.value }.map { items[it.key] }
    }

    inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        private val checkBox: CheckBox = itemView.findViewById(R.id.checkbox)

        fun bind(item: Product, isChecked: Boolean) {
            checkBox.text = item.name
            checkBox.isChecked = isChecked

            // Set a listener to update the checked state when the checkbox is clicked
            checkBox.setOnCheckedChangeListener { _, isChecked ->
                checkedStateMap[adapterPosition] = isChecked
            }
        }
    }
}

data class Product(val id: Int, val name: String, var isChecked: Boolean = false)

